insert  into `template`(`id`,`name`,`group`,`purpose`,`title`,`text`) values (29,'Notificación de nuevo aporte al autor','advice','Mensaje al autor de un proyecto cuando un nuevo aporte','Nuevo aporte al proyecto %PROJECTNAME%','<p>Hola <span class=\"message-highlight-red\">%OWNERNAME%</span>,</p>\r\n<p>El proyecto <span class=\"message-highlight-blue\">%PROJECTNAME%</span> ha recibido un nuevo aporte de %AMOUNT% € de <span class=\"message-highlight-red\">%USERNAME%</span>. %DROPED%</p>\r\n<p>Te recordamos que puedes comunicarte con tus cofinanciadores (individualmente o por grupos) desde tu <span class=\"message-highlight-blue\"><a href=\"%SITEURL%/dashboard\">Panel de control</a></span>.</p>\r\n<p>Seguimos!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinanciación colectiva con ADN abierto<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n');
