insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (4,'en','Personal message from %USERNAME% via Goteo','<p>Dear <span class=\"message-highlight-red\">%TONAME%</span>, <p>\r\n<p>Message sent by <span class=\"message-highlight-red\">%USERNAME%</span> via Goteo:<p>\r\n<blockquote>%MESSAGE%</blockquote>\r\n<p>To send a message to <span class=\"message-highlight-red\">%USERNAME%</span> click <span class=\"message-highlight-blue\"><a href=\"%RESPONSEURL%\">HERE</a></span>.</p>\r\n<p>Don\'t forget that you can see your contacts on Goteo in your profile:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%PROFILEURL%\">%PROFILEURL%</a></span></p>\r\n<p>Cordially,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (4,'eu','%USERNAME%k Goteotik bidalitako mezu pertsonala','<p>Kaixo <span class=\"message-highlight-red\">%TONAME%</span>, <p>\r\n<p><span class=\"message-highlight-red\">%USERNAME%</span>k Goteotik bidalitako mezua:<p>\r\n<blockquote>%MESSAGE%</blockquote>\r\n<p><span class=\"message-highlight-red\">%USERNAME%</span>(r)i mezua bidaltzeko, zapaldu <span class=\"message-highlight-blue\"><a href=\"%RESPONSEURL%\">HEMEN</a></span>.</p>\r\n<p>Gogorarazten dizugu Goteoko zure profilean ikus dezakezula zein pertsonarekin zauden kontaktuan:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%PROFILEURL%\">%PROFILEURL%</a></span></p>\r\n<p>Adeitasunez</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (4,'fr','Message personnel de %USERNAME% depuis Goteo','<p>Bonjour <span class=\"message-highlight-red\">%TONAME%</span>, <p>\r\n<p>Message <span class=\"message-highlight-red\">%USERNAME%</span> depuis Goteo:<p>\r\n<blockquote>%MESSAGE%</blockquote>\r\n<p>Pour envoyer un message à  <span class=\"message-highlight-red\">%USERNAME%</span> cliquer <span class=\"message-highlight-blue\"><a href=\"%RESPONSEURL%\"> ICI </a></span>.</p>\r\n<p>Nous vous invitons à consulter depuis votre profil les gens en contact avec vous:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%PROFILEURL%\">%PROFILEURL%</a></span></p>\r\n<p>Cordialement</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collectif de projets ouverts et libres<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (5,'ca','Confirmació de registre a Goteo','<p>Hola %USERNAME%!</p>\r\n\r\n<p>Moltes gràcies pel teu interès per Goteo, el teu compte ha estat creat amb èxit :)</p>\r\n\r\n<p>Per confirmar la teva adreça de correu electrònic i completar el registre, fes clic al següent enllaç (o copia\'l a la barra del navegador):</p>\r\n\r\n<a href=\"%ACTIVATEURL%\"><span class=\"message-highlight-red\">%ACTIVATEURL%</span></a>\r\n\r\n<p>Et convidem a recolzar aquesta aposta pel crowdfunding obert cofinançant, dins de les teves possibilitats, alguna de les iniciatives que trobaràs destacades a la nostra portada (https://goteo.org/). Ajudaràs així a fer realitat els fantàstics retorns col·lectius que proposen.</p>\r\n\r\n<p>Salutacions!</p>\r\n\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identi.ca: @goteofunding\r\n</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (5,'en','Goteo Registration Confirmation','<p>Dear %USERNAME%!</p>\r\n\r\n<p>Thank you very much for your interest in Goteo. Your account has been successfully created :)</p>\r\n\r\n<p>In order to confirm your email address and complete the registration, please click on the following link (or copy it to your browser\'s address bar):</p>\r\n\r\n<a href=\"%ACTIVATEURL%\"><span class=\"message-highlight-red\">%ACTIVATEURL%</span></a>\r\n\r\n<p>We invite you to support this effort toward open, co-financed crowdfunding as much as your circumstances permit, perhaps through one of the proposals that you\'ll find highlighted on the front page of http://goteo.org/ when you log in. In that way, you\'ll help make real the fantastic collective returns that are proposed.</p>\r\n\r\n<p>Cheers,<br>\r\nThe Goteo Mailer <br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identi.ca: @goteofunding\r\n</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (5,'eu','Goteon erregistroa baieztatzea','<p>Kaixo %USERNAME%!</p>\r\n\r\n<p>Eskerrik asko Goteo dela-eta daukazun interesarengatik. Zure kontua arrakastaz sortu da :)</p>\r\n\r\n<p>Zure posta elektronikoko helbidea baieztatzeko eta erregistroa osatzeko, egin klik hurrengo estekan (edo kopia ezazu nabigatzaileko helbide-barran):</p>\r\n\r\n<a href=\"%ACTIVATEURL%\"><span class=\"message-highlight-red\">%ACTIVATEURL%</span></a>\r\n\r\n<p>Gonbita egiten dizugu cowdfunding irekiaren aldeko apustu hau babes dezazun, zure ahalbideekin http://goteo.org/-en azalean aurkituko dituzun ekimenetako bat kofinantziatuz. Horrela, proposatzen diren itzulera kolektibo miragarriak egia bihurtuko dituzu.</p>\r\n\r\n<p>Agur!</p>\r\n\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identi.ca: @goteofunding\r\n</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (5,'fr','Confirmation d\'inscription à Goteo','<p>Bonjour %USERNAME%!</p>\r\n\r\n<p>Votre compte a été créé avec un succès. Merci beaucoup pour l\'intérêt que vous portez à Goteo</p>\r\n\r\n<p>Pour confirmer votre inscription, regardez le lien envoyé par mail (ou copiez l\'adresse dans la barre de votre navigateur):</p>\r\n\r\n<a href=\"%ACTIVATEURL%\"><span class=\"message-highlight-red\">%ACTIVATEURL%</span></a>\r\n\r\n<p>A bientôt</p>\r\n\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collectif de projets ouverts et libres<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identi.ca: @goteofunding\r\n</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (6,'ca','Petició de recuperació de contrasenya a Goteo','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Hem rebut una petició per recuperar la contrasenya del teu compte d\'usuari a <a href=\"https://goteo.org/\">https://goteo.org/.</a></p>\r\n<p>Per accedir al teu compte i canviar la contrasenya utilitza el següent enllaç:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%RECOVERURL%\">%RECOVERURL%</a></span></p>\r\n<p>Si no pots fer clic, copia\'l i enganxa\'l al navegador.</p>\r\n<p>(En cas que no hagis demanat aquest canvi de contrasenya, uhm... ignora aquest missatge).</p>\r\n<p>Cordialment,</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (6,'en','Goteo password recovery request','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We received a request to recover the password for your user account on <a href=\"http://goteo.org/\">http://goteo.org/.</a></p>\r\n<p>To access your account and change the password, use the following link:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%RECOVERURL%\">%RECOVERURL%</a></span></p>\r\n<p>If the link doesn\'t work from here, copy it and paste it into your browser.</p>\r\n<p>(If you didn\'t ask to have your password reset, simply ignore this message).</p>\r\n<p>Cordially,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (6,'eu','Goteoko pasahitza berreskuratzeko eskaria','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure eskaria jaso dago <a href=\"http://goteo.org/\">http://goteo.org/</a>-n daukazun erabiltzaile-kontuaren pasahitza berreskuratzeko.</p>\r\n<p>Zure kontura sartu eta pasahitza aldatzeko, erabil ezazu hurrengo esteka:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%RECOVERURL%\">%RECOVERURL%</a></span></p>\r\n<p>Klik egiterik ez baduzu, kopiatu eta itsats ezazu nabigatzailean.</p>\r\n<p>(Pasahitza aldatzeko eskatu ez baduzu, umm... ez egin kasurik mezu honi).</p>\r\n<p>Adeitasunez,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (6,'fr','Demande de renvoi de mot de passe Goteo','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p> Nous avons reçus une demande de renvoi du mot de passe de votre compte d\'utilisateur <a href=\"http://goteo.org/\">http://goteo.org/.</a></p>\r\n<p>Pour accéder à votre compte et modifier le mot de passe, cliquez sur le lien suivant:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%RECOVERURL%\">%RECOVERURL%</a></span></p>\r\n<p>Si le lien ne s\'ouvre pas, vous pouvez le copier/coller dans la barre dans la barre d\'adresse de votre navigateur</p>\'\r\n<p>(Si jamais vous n\'êtes pas l\'auteur de cette demande, alors ... ignorez ce message ).</p>\r\n<p>Cordialment,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (7,'ca','Sol.licitud de canvi de correu electrònic a Goteo','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Hem rebut una petició per canviar el correu electrònic del teu compte d\'usuari a Goteo.org</p>\r\n<p>Per confirmar la propietat de la teva nova adreça de correu electrònic, fes clic al següent enllaç (o copia\'l a la barra de direcció del navegador):</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%CHANGEURL%\">%CHANGEURL%</a></span></p>\r\n<p>Aquest procés és necessari per confirmar la propietat de l\'adreça de correu electrònic, així que no podràs operar a Goteo amb aquesta direcció fins que l\'hagis confirmat.</p>\r\n<p>Cordialment,</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (7,'en','Goteo email change request','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We received a request to change the email account associated with your Goteo.org user account.</p>\r\n<p>In order to confirm that this new email address belongs to you, please click on the following link (or copy it to your browser\'s address bar):</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%CHANGEURL%\">%CHANGEURL%</a></span></p>\r\n<p>This process is necessary in order to confirm that the new email address is really yours, so you won\'t be able to use this email on Goteo.org until it has been confirmed.</p>\r\n<p>Cordially,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (7,'eu','Goteoko posta elektronikoa aldatzeko eskaria','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure eskaria jaso dago Goteo.org-n daukazun posta elektronikoa aldatzeko.</p>\r\n<p>Zure posta elektronikoko helbidearen jabetza baieztatzeko, egin klik hurrengo estekan (edo kopia ezazu nabigatzaileko helbide-barran).</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%CHANGEURL%\">%CHANGEURL%</a></span></p>\r\n<p>Prozesu hau beharrezkoa da posta elektronikoko helbidearen jabetza baieztatzeko; horrenbestez, ezin izango duzu helbide honekin lan egin bera baieztatu baino lehen.</p>\r\n<p>Adeitasunez,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (7,'fr','Demande de changement d\'adresse e-mail sur Goteo','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Hemos recibido una petición para cambiar el email de tu cuenta de usuario en Nous avons reçu une demande de changement de l\'adresse E-mail de votre compte sur Goteo.org</p>\r\n<p>Afin de confirmer que vous êtes le détenteur de cette nouvelle adresse courriel, vous devez cliquer sur le lien suivant (ou bien le copier/cller dans la barre d\'adresse de votre navigateur):</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%CHANGEURL%\">%CHANGEURL%</a></span></p>\r\n<p>Cette étape est nécessaire afind e confirmer le changement d\'adresse électronique: vous ne pourrez pas utiliser cette nouvelle adresse tant que vous ne l\'aurez pas confirmée.</p>\r\n<p>Cordialement,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nle Financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (8,'ca','El projecte %PROJECTNAME% ha passat a fase de valoració','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Hem rebut la petició de revisar el projecte <span class=\"message-highlight-blue\">%PROJECTNAME%</span> per valorar la seva publicació i promoció mitjançant Goteo.</p>\r\n<p>Aviat ens posarem en contacte amb tu.</p>\r\n<p>Pots trobar més informació sobre el procés de revisió de projectes dins les nostres FAQ: <span class=\"message-highlight-blue\"><a href=\"https://www.goteo.org/faq/sponsor#q88\">https://www.goteo.org/faq/sponsor#q88</a></span></p>\r\n<p>Moltes gràcies!</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (8,'en','The %PROJECTNAME% project is now in the evaluation stage','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We have received your request to look over the <span class=\"message-highlight-blue\">%PROJECTNAME%</span> project in order to evaluate whether Goteo should publish and promote it.</p>\r\n<p>We will get in touch with you shortly about this, in order to comment\r\nand complete the information necessary for your campaign.</p>\r\n<p>You can find more information about the project evaluation process in our FAQ: <span class=\"message-highlight-blue\"><a href=\"/faq/sponsor#q88\">FAQ#88</a></span></p>\r\n<p>Many thanks!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (8,'eu','%PROJECTNAME% proiektua balorazio fasean dago','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure eskaria jaso dugu <span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektua berrikusteko, bera Goteoren bidez argitaratu eta sustatzea baloratze aldera.</p>\r\n<p>Hemendik gutxira lantaldeko kideren bat zurekin jarriko da harremanetan horri buruz hitz egiteko.</p>\r\n<p>Proiektuak berrikusteko prozesuari buruzko informazio gehiago gure FAQetan topatuko duzu: <span class=\"message-highlight-blue\"><a href=\"http://www.goteo.org/faq/sponsor#q88\">http://www.goteo.org/faq/sponsor#q88</a></span></p>\r\n<p>Eskerrik asko!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (8,'fr','Le projet %PROJECTNAME% a passé dans la phase d\'évaluation','<p>Salut <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Nous avons été invités à examiner le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> afin d\'évaluer sa publication et sa promotion par Goteo. </p>\r\n<p>Bientôt quelqu\'un de l\'équipe prendra contact avec vous à ce sujet.</p>\r\n<p>Vous pouvez trouver plus d\'informations sur le processus de révision des projets dans notre FAQ: <span class=\"message-highlight-blue\"><a href=\"http://www.goteo.org/faq/sponsor#q88\">http://www.goteo.org/faq/sponsor#q88</a></span></p>\r\n<p>Merci beaucoup!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collective avec du ADN ouvert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (8,'it','El progetto %PROJECTNAME% è in fase di valutazione','<p>Ciao <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Abbiamo ricevuto la richiesta di ripassare il progetto <span class=\"message-highlight-blue\">%PROJECTNAME%</span> per valutare la sua pubblicazione e e diffusione su Goteo. </p>\r\n<p>Tra poco ti contatteremo per integrare l\'informazione necessaria per la campagna \r\n</p>\r\n<p>Maggiori informazioni sui progetti in fase di valutazione nelle nostre FAQ: <span class=\"message-highlight-blue\"><a href=\"http://www.goteo.org/faq/sponsor#q88\">http://www.goteo.org/faq/sponsor#q88</a></span></p>\r\n<p>Grazie!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinanciación colectiva con ADN abierto<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (9,'ca','Sol.licitud de baixa a Goteo','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Hem rebut una sol·licitud per donar de baixa el teu compte d\'usuari a <a href=\"https://goteo.org/\">https://goteo.org/</a></p>\r\n<p>Per completar el procés de baixa accedeix al següent enllaç:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%URL%\">%URL%</a></span></p>\r\n<p>Si no pots fer clic, copia\'l i enganxa\'l al navegador.</p>\r\n<p>(En cas que no hagis demanat aquesta baixa, uhm... ignora aquest missatge)</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (9,'en','Goteo account closure request','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We have received a request to close your account on <a href=\"http://goteo.org/\">http://goteo.org/</a></p>\r\n<p>To complete the closure of your account, please click the following link:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%URL%\">%URL%</a></span></p>\r\n<p>You can also copy the link and paste it in your browser\'s address bar.</p>\r\n<p>(If you did not request this account closure, please disregard this message.)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (9,'eu','Goteon baja eskatzeko eskaria','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure eskaria jaso dago <a href=\"http://goteo.org/\">http://goteo.org/</a>-n daukazun erabiltzaile-kontuari baja emateko.</p>\r\n<p>Baja emateko prozesua osatzeko, sartu hurrengo estekan:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%URL%\">%URL%</a></span></p>\r\n<p>Klik egiterik ez baduzu, kopiatu eta itsats ezazu nabigatzailean.</p>\r\n<p>(Baja eskatu ez baduzu, umm... ez egin kasurik mezu honi).</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (9,'fr','Demande de résiliation sur Goteo','<p>Bonjour<span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Nous avons reçu une demande de résiliation de votre compte utilisateur sur <a href=\"http://goteo.org/\">http://goteo.org/</a></p>\r\n<p>Pour compléter le processus de retrait accèdez au lien suivant:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%URL%\">%URL%</a></span></p>\r\n<p>Si vous ne pouvez pas cliquer, vous pouvez copier-coller le lien dans votre navigateur.</p>\r\n<p>(Dans le cas où vous n’avez pas effectué cette demande, uhm... ignorez ce message)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collectif à l\'ADN ouvert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (10,'ca','Gràcies per cofinançar el projecte %PROJECTNAME%','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Moltes gràcies per cofinançar el projecte <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> amb %AMOUNT% €. %DROPED%</p>\r\n\r\n<p>Et recordem que has seleccionat la següent recompensa, segons es descriu en l\'apartat de la pàgina sota el concepte: <br> %REWARDS%</p><p>Important: Aquest retorn es farà efectiu considerant el seu temps de producció, i únicament si el projecte obté el finançament mínim requerit.</p>\r\n\r\n<p>Aprofitem aquest email per donar-te alguns detalls del funcionament de les transaccions a través de Goteo. </p>\r\n<p>Com hauràs pogut veure en el moment de triar la forma de pagament, hi ha dos sistemes possibles: PayPal i targeta de crèdit.</p>\r\n\r\n<p>En cas que hagis utilitzat PayPal el càrrec no serà realitzat fins que finalitzi la 1a ronda del projecte (40 dies) i aquest hagi obtingut el mínim del pressupost que necessita reunir. Aquest procés és una preautorització del cobrament de l\'import que hagis decidit, que només es farà efectiu si es compleixen aquestes condicions. Podria definir-se com un compromís de pagament si el projecte té prou recolzament o \"promeses de suport\" :-) </p>\r\n\r\n<p>Si has utilitzat el sistema de pagament amb targeta de crèdit el procediment és diferent: el càrrec s\'executa immediatament en el compte corresponent, i si el projecte al qual has donat suport no arribés al finançament mínim, la teva aportació et serà reemborsada (sense cap cost addicional) de manera automàtica en el mateix compte que has utilitzat per fer la transacció.</p>\r\n\r\n<p>Et recordem que el teu aport és una donació desgravable en la declaració a Hisenda, tal com t\'expliquem aquí:<a href=\"https://goteo.org/faq/investors#q112\">https://goteo.org/faq/investors#q112</a></p>\r\n\r\n<p>Que tinguis un bon dia!</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (10,'en','Thank you for co-financing the %PROJECTNAME% project','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Thank you very much for co-financing the <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> project in the amount of %AMOUNT% €.</p>\r\n\r\n<p>We remind you that you have chosen the following reward, as described in the project\'s page: <br>  %REWARDS%</p><p>Important: This reward will be effective considering its period of production, and only if it obtains the minimum funding required.</p>\r\n\r\n<p>We\'d also like to take advantage of this correspondence to give you some details about how bank transactions work. </p>\r\n<p>As you will have seen when it came to choosing a form of payment, there are two possible methods: PayPal and credit card. </p>\r\n\r\n<p>If you chose PayPal, the charge will not be made until the end of the first round of financing for the project (40 days) and only if the project has received the minimum budget required. This process is a preauthorization to collect the amount you decided only if those conditions are fulfilled. You might say it\'s a commitment to pay as long as the project gets enough support (or promises of support) :-) </p>\r\n\r\n<p>If you used a credit card, the process is different: the charge is made immediately to the corresponding account, and at the end of the first round of financing, if the project that you have supported does not achieve its minimum financing, your contribution is refunded (at no additional cost) to the same account used in the original transaction. </p>\r\n\r\n<p>Have a good day!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (10,'eu','Eskerrik asko %PROJECTNAME% proiektua kofinantziatzeagatik','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Eskerrik asko <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> proiektua %AMOUNT% eurorekin kofinantziatzeagatik.</p>\r\n\r\n<p>Gogorarazten dizugu honako saria hautatu duzula, orrialdean %REWARDS% kontzeptuaren pean azaltzen den moduan.</p><p>Garrantzitsua: Itzulera hori gauzatuko da haren produkzio denbora kontuan hartuta, eta soilik behar den gutxieneko finantzaketa lortzen bada.</p>\r\n\r\n<p>Mezu hau aprobetxatu nahi dugu Goteoren bidezko transakzioen funtzionamenduari buruzko xehetasun batzuk emateko. </p>\r\n<p>Ordaintzeko modua hautatzeko unean ikusi duzun bezala, bi sistema daude aukeran: PayPal eta kreditu txartela.</p>\r\n\r\n<p>PayPal sistema erabili baduzu, kargua ez zaizu egingo harik eta proiektuko 1. txanda amaitu arte (40 egun) eta proiektuak bildu beharreko gutxieneko aurrekontua lortu arte. Prozesu hau aurre-baimen bat da, hautatu duzun zenbatekoa kobratzekoa, baldin eta aipatutako baldintzak betetzen badira. Ordaintzeko konpromiso baten moduan defini daiteke, betiere proiektuak behar adina babes edo “babesa emateko agindu” biltzen baldin baditu :-)</p>\r\n\r\n<p>Kreditu txartelaren bidez ordaintzeko sistema erabili baduzu, prozedura bestelakoa da: kargua berehala egingo zaizu kasuko kontuan, eta babestu duzun proiektua ez bada gutxieneko finantzaketara iristen, egindako ekarpena itzuliko zaizu (inongo kostu gehigarririk gabe) transakzioa egiteko erabili duzun kontu berean.</p>\r\n\r\n<p>Egun ona izan dezazula!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (10,'fr','Merci d\'avoir cofinancé le projet %PROJECTNAME%','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Merci beaucoup d\'avoir cofinancé le projet <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> pour un montant de %AMOUNT% €.</p>\r\n\r\n<p>Nous vous rappelons que vous avez choisi la contrepartie suivante: <br>  %REWARDS%</p><p>Important: Celle-ci vous sera remise dans les meilleurs délais au regard des contraintes du projet, et seulement si le projet obtient le financement global demandé.</p>\r\n\r\n<p>Nous profitons de cet e-mail pour vous donner quelques détails sur la façon dont les transactions sont effectuées par Goteo. </p>\r\n<p>Comme vous avez pu le voir au moment de choisir le mode de paiement, il y a deux moyens possibles: PayPal et la carte de crédit. </p>\r\n\r\n<p>Si vous avez utilisé PayPal, le débit ne sera pas effectué avant la fin du 1er tour du projet (40 jours) et cela seulement si l\'objectif financier de la campagne est atteint. C\'est en quelque sorte un \"engagement à payer\" si le projet reçoit suffisamment de soutien ou de «promesses de soutien\" :-) </p>\r\n\r\n<p>Si vous avez utilisé le système de paiement par carte de crédit, la procédure est différente: la charge est débitée immédiatement sur le compte lié à la carte, et si le projet que vous avez soutenu n’atteint pas ses objectifs de financement, votre contribution sera remboursée (sans aucun coût supplémentaire ) sur le même compte que celui utilisé pour effectuer la transaction. </p>\r\n\r\n<p>Nous vous souhaitons une bonne journée!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collectif à l\'ADN ouvert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
