insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (12,'fr','Réponse à votre message sur le projet %PROJECTNAME%','<p>Bonjour <span class=\"message-highlight-red\">%OWNERNAME%</span>!</p>\r\n<p><span class=\"message-highlight-red\">%USERNAME%</span> a répondu à votre message dans le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span></p>\r\n<blockquote>%MESSAGE%</blockquote>\r\n<p>Pour envoyer un message privé <span class=\"message-highlight-red\">%USERNAME%</span> cliquez <span class=\"message-highlight-blue\"><a href=\"%RESPONSEURL%\">AQUÍ</a></span>.</p>\r\n<p>Vous pouvez afficher tous les messages sur la page du projet <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTURL%</a></span>.</p>\r\n<p>Cordialement,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement collectif à l\'ADN ouvert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (13,'ca','Al projecte %PROJECTNAME% li falten 8 dies per ser arxivat','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Et recordem que al projecte <span class=\"message-highlight-blue\">%PROJECTNAME%</span>li falten 8 dies perquè finalitzi la seva campanya a Goteo i encara no ha aconseguit el mínim de cofinançament.</p>\r\n<p>Sabem que no és fàcil, i que ja has tractat d\'activar diversos fronts en aquest sentit, però aprofitant aquest missatge et convidem a fer més difusió per aconseguir aquestes imprescindibles aportacions. Un recordatori a les persones i comunitats a qui els pot interessar cofinançar-lo, un post a l\'apartat /Novetats del bloc del projecte a Goteo, fins i tot un breu missatge als teus cofinançadors <a href=\"https://goteo.org/dashboard/projects/rewards\">des d\'aquí</a> convidant-los a que reenviïn l\'enllaç als seus contactes, són accions que encara poden donar els seus fruits. Si vols escriu-nos i les comentem...</p>\r\n<p>Ànims!</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (13,'en','The %PROJECTNAME% project will close in 8 days','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>This is to inform you that the <span class=\"message-highlight-blue\">%PROJECTNAME%</span> project has 8 days left until its Goteo campaign draws to a close, but unfortunately it has yet to achieve its minimum financing.</p>\r\n<p>We know it\'s not easy, and that you have tried various strategies to this end, but we\'re taking advantage of this opportunity to encourage you to campaign even harder to obtain those necessary contributions.</p>\r\n<p>We remind you that you can find the widget for marketing your project on your <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span></p>\r\n<p>Good luck!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (13,'eu','%PROJECTNAME% proiektuari 8 egun geratzen zaizkio iraungitzeko','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Jakitera ematen dizugu <span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektuari 8 egun falta zaizkiola Goteon egindako kanpaina amaitzeko eta oraindik ere ez duela gutxieneko kofinantziazioa lortu.</p>\r\n<p>Badakigu ez dela erraza, eta zentzu berean zenbait gauza aktibatzen ahalegindu zarela, baina proiektua laster iraungitzeari buruzko mezu hau aprobetxatuz, gonbita egiten dizugu nahitaezkoak diren ekarpen horiek lortzeko.</p>\r\n<p>Gogorarazten dizugu proiektua argitaratzeko widgeta zure <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>en aurkituko duzula.</p>\r\n<p>Aurrera!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (13,'fr','Il manque 8 jours pour que le projet %PROJECTNAME% soit archivé','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>S\'il vous plaît noter qu’il manquent  huit jour pour que le projet  <span class=\"message-highlight-blue\">%PROJECTNAME%</span>finalise sa campagne Goteo et il n\'a pas encore atteint le cofinancement minimum.</p>\r\n<p>Nous savons qu\'il n\'est pas facile, et que vous avez déjà essayé d’activer des initiatives à cet égard, mais on veut profiter cet avis de que la campagne est au point d\'expirer pour vous encourager à faire plus de sensibilisation pour obtenir ces contributions essentielles. Un rappel aux gens et à les communautés qui pourraient être intéressés à cofinancer, un post sur Blog / Nouvelles du projet, même un message à vos cofinanceurs depuis ici  <a href=\"http://goteo.org/dashboard/projects/rewards\">depuis ici</a> en les invitant a partager le lien avec ses contacts, ceux sont des actions qui peuvent encore donner des fruits. Si vous voulez, écrivez nous et on en parle...</p>\r\n<p>Courage!</p>\r\n\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (14,'en','The %PROJECTNAME% project will close in 1 day','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Your project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, will close in 1 day and you still have not yet obtained the minimum co-financing required. But you have gotten to 70%, and so all is not yet lost.</p>\r\n<p>You should try to obtain those necessary contributions as urgently as you can, by explaining its importance to the people in your network and circles.</p>\r\n<p>Remember that you can find the widget for marketing your project on your <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span></p>\r\n<p>Good luck!</p>\r\n<p>The Happy Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (14,'eu','%PROJECTNAME% proiektuari egun 1 geratzen zaio iraungitzeko','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure <span class=\"message-highlight-blue\">%PROJECTNAME%</span> lproiektuari egun 1 geratzen zaio iraungitzeko, eta oraindik ere ez du lortu ezarritako kofinantzaketa. Alabaina, % 70era iritsi da; horrenbestez, oraindik ere ez da guztia galdu.</p>\r\n<p>Ahaleginak egin beharko zenituzke nahitaezko ekarpen horiek presaz lortzeko, zure sareko pertsonak aktibatuz edo haiei proiektuaren garrantzia gogoraraziz.</p>\r\n<p>Gogorarazten dizugu zure proiektua argitaratzeko widgeta zure <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>en aurkituko duzula.</p>\r\n<p>Zorte handia!</p>\r\n<p>The Happy Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (14,'fr','Il manque 1 jour pour que le projet  %PROJECTNAME% soit archivé','<p>Bonjour<span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Il ne manque que un jour pour que votre projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> expire et il n\'a pas encore atteint le cofinancement minimum fixé. Mais il a atteint déjà le 70% et, par conséquent, tout n\'est pas encore perdu..</p>\r\n<p>Vous devriez essayer d\'obtenir ces apports essentiels avec urgence, en activant vos réseaux et en leur rappelant de son importance, en cherchant d\'alternatives ou des soutiens substantielles qui puissent aider à pousser le thermomètre et encourager à plus de gens dans cette dernière ligne droite.</p>\r\n<p>Bonne chance!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (15,'en','The project, %PROJECTNAME%, has passed to the second round in Goteo','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Thanks to your contribution and that of others, the project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, has been successfully co-financed and is continuing on in the second round!</p>\r\n<p>But this is not the end, you can still do more for the common good and shared resources by closely following the evolution of <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, that will now enter into the production phase. You can also go to http://www.goteo.org/ to discover other important projects that interest you that you can co-finance or otherwise support on their way to becoming reality.</p>\r\n<p>Remember that you can still participate in the forums and community for <span class=\"message-highlight-blue\">%PROJECTNAME%</span> via <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTURL%</a></span>.</p>\r\n<p>Keep in touch! :)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (15,'eu','%PROJECTNAME% proiektua bigarren txandara pasa da Goteon','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zure ekarpenari eta bete pertsona batzuen ekarpenei esker, <span class=\"message-highlight-blue\">%PROJECTNAME%</span> arrakastaz kofinantziatu da eta bigarren txandara pasa da!</p>\r\n<p>Baina hau ez da hemen amaitzen. Oraindik ere gehiago egin dezakezu gauza komunaren eta baliabide partekatuen alde, orain produkzio fasean dagoen <span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektuaren bilakaera hurbiletik jarraituz. Edo http://www.goteo.org/ webgunera sartuz interesatzen zaizkizun eta egia bihurtzeko kofinantziatu edo bestela lagun ditzakezun beste proiektu batzuk deskubritzeko.</p>\r\n<p>Gogorarazten dizugu elkarrizketetan eta <span class=\"message-highlight-blue\">%PROJECTNAME%</span> erkidegoan <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTURL%</a></span>-etik parte har dezakezula.</p>\r\n<p>Aurrera jarraitzen dugu :)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (15,'fr','Le projet %PROJECTNAME% se lance dans la seconde phase de sa campagne Goteo','<p>Bonjour<span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Grâce à votre contribution et à celles de toute une communauté, <span class=\"message-highlight-blue\">%PROJECTNAME%</span> a réussi la première phase de sa campagne de crowdfunding et accède au second tour de financement!</p>\r\n<p>Ca ne s\'arrête donc pas là! Vous pouvez toujours faire plus pour le bien commun et le partage de connaissances en suivant dès prés l\'évolution de <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, qui passe maintenant dans la phase de production. Vous pouvez également accéder à http://www.goteo.org/ pour découvrir d\'autres campagnes intéressantes que vous pourriez soutenir financièrement ou autrement, en donnant un coup de main par exemple.</p>\r\n<p>Nous vous rappelons que vous pouvez prendre part aux conversations relatives à <span class=\"message-highlight-blue\">%PROJECTNAME%</span> depuis <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTURL%</a></span>.</p> et rejoindre ainsi la communauté de ce projet. \r\n<p>On continue :)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
