insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (16,'fr','Le projet %PROJECTNAME% a achevé le deuxième tour','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Grâce à votre contribution, le projet  <span class=\"message-highlight-blue\">%PROJECTNAME%</span> a achevé la seconde phase et ainsi a réussi à élargir son financement!</p>\r\n<p>Nous vous rappelons que vous pouvez participer à la communauté du projet sur <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTURL%</a></span></p>\r\n<p>Cordialemente,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (17,'en','The project, %PROJECTNAME%, was not able to obtain its minimum financing on Goteo :(','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Despite your appreciated contribution, that we are very grateful for, we are contacting you to let you know that the project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, was not able to obtain the necessary co-financing that it needed in Goteo, and the project has been archived.</p>\r\n<p>Nevertheless, we hope you will consider the possibility of co-financing—with the same amount, or whatever amount that you wish—another project in our campaign.</p>\r\n<p>There are other interesting and open projects, in search of different kinds of help and support that need to be developed through the Goteo community, of which we consider you an important member. You can find them in order of publication on http://www.goteo.org/discover/view/recent</p>\r\n<p>Affectionately,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (17,'eu','%PROJECTNAME% proiektua kez du lortu Goteon zeukan gutxieneko helburua :(','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Egin zenuen eta itzuli nahi dizugun ekarpen eskergaitza gorabehera, zuregana jotzen dugu jakinarazteko <span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektuak ez duela amaieran Goteon behar zuen kofinantzaketarako gutxieneko laguntza, eta artxibatu egin dela.</p>\r\n<p>Hala eta guzti zere, gustatuko litzaiguke zuk aintzat hartzea kopuru berarekin, edo egokitzat jotzen duzunarekin, gaur egun kanpainan dauden proiektuetatik beste bat kofinantziatzeko aukera.</p>\r\n<p>Proposamen berdin interesgarri eta irekiak dira, zu partaide zaitugun Goteoren erkidegoaren bitartez garatzeko laguntzak bilatzen dituztenak. http://www.goteo.org/discover/view/recent helbidean aurkituko dituzu berritasun hurrenkeraren arabera.</p>\r\n<p>Adeitasunez,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (17,'fr','Le projet  %PROJECTNAME% n\'a pas atteint son objectif minimal sur Goteo :(','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>En dépit de votre précieuse contribution, que nous voulons vous remercier encore une fois, nous prenons contact pour vous informer que le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> n’a pas finalement obtenu le soutien minimum de cofinancement nécessaire sur Goteo et il a été archivé.</p>\r\n<p>Cependant, nous aimerions vous suggérer la possibilité de cofinancer avec le même montant, ou ce que vous jugez approprié, d\'autres projets en cours.</p>\r\n<p> Il s’agit également de propositions intéressantes et ouvertes, à la recherche de soutien pour se développer grâce à la communauté de Goteo, dont on considére vous faites partie. Vous pouvez les trouver dans l\'ordre de nouveauté dans http://www.goteo.org/discover/view/recent</p>\r\n<p>Cordialement,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (18,'en','%PROJECTNAME%, has news','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We\'d like you to know that the project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, that you are co-financing, has published some news.</p>\r\n%POST%\r\n<p>You can read it in <span class=\"message-highlight-blue\"><a href=\"%UPDATEURL%\">%UPDATEURL%</a></span></p>\r\n<p>If you would like to receive (or not receive) further updates about this project, remember that you can change your notification settings from your Goteo dashboard.</p>\r\n<p>Cordially,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (18,'eu','%PROJECTNAME% berritasun batzuk argitaratu ditu','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>!</p>\r\n<p>Jakitera ematen dizugu kofinatziatzen ari zaren <span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektuak berritasunen bat argitaratu duela.</p>\r\n%POST%\r\n<p><span class=\"message-highlight-blue\"><a href=\"%UPDATEURL%\">%UPDATEURL%</a></span>-ean ikus dezakezu.</p>\r\n<p>Horri buruzko gaurkotze gehiagorik ez jasotzea nahiago baduzu, jakinarazpen hauen konfigurazioa alda dezakezu Goteoko kontrol paneletik.</p>\r\n<p>Aurrera!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (18,'fr','%PROJECTNAME% a publié des nouvelles','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>!</p>\r\n<p>S\'il vous plaît, notez que le projet que vous cofinancez, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, a publié des nouvelles.</p>\r\n%POST%\r\n<p>Vous pouvez le voir en <span class=\"message-highlight-blue\"><a href=\"%UPDATEURL%\">%UPDATEURL%</a></span>.</p>\r\n<p>Si vous préférez ne pas recevoir de nouvelles mises à jour à ce sujet, vous pouvez modifier les paramètres de notification à partir de votre panneau de contrôle Goteo.</p>\r\n<p>On continue,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (19,'en','The project, %PROJECTNAME%, is 20 days into its campaign!','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>The project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, is 20 days into its financing campaign. We think this midpoint is a good time to think about all that has been achieved so far. Depending on how the contributions and offered collaborations are going, we encourge you to take advantage of the date in order to spread the word and explain what else needs to happen to convert this project into reality.</p>\r\n<p>Remember that you can find the marketing widget for your project on your <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>.</p>\r\n<p>We\'ll be in touch!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (19,'eu','%PROJECTNAME% proiektuak 20 egun daramatza kanpainan','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p><span class=\"message-highlight-blue\">%PROJECTNAME%</span> proiektuak 20 egun daramatza kanpainan. Esan dezagun haren ekuatorean dagoela eta une egokia dela orain arte lortutakoaren balantzea egiteko. Bilketa eta emandako laguntzak nola dauden ikusita, gonbita egiten dizugu data hau aprobetxa dezazun haren berri emateko, adieraziz oraindik ere zer behar den proiektua errealitate bihurtzeko.</p>\r\n<p>Halaber, gonbita egiten dizugu proiektuen zabalkunderako wikin aipatzen duguna kontuan har dezazun, zure buruari oraindik ere planteatu ez diozun zerbait baldin badago:<br><br>\r\nhttp://wiki.goteo.org/mediawiki/index.php?title=Guerrilla_de_comunicaci%C3%B3n_amable_para_tu_proyecto_en_Goteo</p>\r\n<p>Gogorarazten dizugu zure proiektua argitaratzeko widgeta zure <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>en aurkituko duzula.</p>\r\n<p>Aurrera!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (19,'fr','Ça fait 20 jour que le projet %PROJECTNAME% est en campagne','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Ça fait 20 jour que le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> est en campagne. Disons que celui représente son équateur et un bon moment pour faire le point sur les résultats obtenus jusqu\'à présent. Nous vous invitons à profiter de la date pour la diffusion du projet en expliquant que c’est qu’il a encore besoin pour se réaliser, toujours en relation au financement et au soutien déjà obtenu.</p>\r\n<p>C’est bien aussi de considérer les commentaires sur le décalogue pour la diffusion de projets <a href=”http://goteo.org/blog/482”>decálogo para difusión de proyectos</a> (dans le cas où il y a quelque chose que vous n\'avez pas encore soulevé), ou bien de nous contacter pour discuter de la façon d\'aborder la campagne.</p>\r\n<p>On continue!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (20,'en','The project, %PROJECTNAME%, has made it to the second round!','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Congratulations! Your project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, has made it to the second round of co-financing and support on Goteo. Of course, this doesn\'t end here, and indeed in a certain sense, it is just the beginning. From this point forward, you have 40 additional days to work on getting your project off the ground, communicating as much as you can and generating as much buzz and community around it as is possible, and in that way, obtaining the additional support that you need to carry it to the optimum level.</p>\r\n<p>Remember that you can find the marketing widget for your project on your <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>.</p>\r\n<p>Good luck in the second round!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (20,'eu','%PROJECTNAME% proiektuak bigarren txandara iritsi da','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Zorionak! <span class=\"message-highlight-blue\">%PROJECTNAME%</span> izeneko zure proiektua kofinantzaketarako eta laguntzetarako  bigarren txandara iritsi da Goteon. Horrenbestez, hau ez da hemen bukatzen, hasi besterik ez baita egin. Hemendik aurrera, 40 egun gehiago dauzkazu zure proiektua abiatzen lan egiteko, haren berri ahal den guztian ematen eta haren inguruan ikusgarritasun eta erkidego handiagoak lortuz; hartara, izan ere, behar dituzun laguntza gehigarriak lortuko dituzu hura maila optimora eramateko.</p>\r\n<p>Gogorarazten dizugu zure proiektua argitaratzeko widgeta zure <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>en aurkituko duzula.</p>\r\n<p>Zorte handia izan dezazula bigarren txandan!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (20,'fr','El proyecto %PROJECTNAME% ha pasado a segunda ronda','<p>Bonjour<span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Félicitations! Le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> est passé au deuxième tour de financement et de soutien sur Goteo. Donc, ce ne finit pas ici, sinon juste au contraire, ce ne vient que de commencer. <p></p>A partir de maintenant vous avez 40 jours supplémentaires pour commencer à travailler sur votre projet, communiquer tout ce qu’il vous soit possible et ainsi générer plus d\'expectation et grandir la communauté autour de lui pour obtenir le soutien supplémentaire dont vous avez besoin pour atteindre le niveau optimal.</p>\r\n<p>Nous vous rappelons que vous pouvez trouver le widget pour annoncer votre projet sur votre Dashboard <span class=\"message-highlight-blue\"><a href=\"%WIDGETURL%\">Dashboard</a></span>.</p>\r\n<p>Bonne chance au second tour!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (21,'en','The project, %PROJECTNAME%, has closed','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>We are sorry that your project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, closed without obtaining the minimum support during the time limit you had. We hope that it has still been a good experience and we invite you to continue participating in Goteo and thinking about ways in which it can be useful to you.</p>\r\n<p>You can consult the project summary on your <span class=\"message-highlight-blue\"><a href=\"%SUMMARYURL%\">Dashboard</a></span>.</p>\r\n<p>Cordially,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (21,'eu','%PROJECTNAME% proiektua iraungi da','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Sentitzen dugu <span class=\"message-highlight-blue\">%PROJECTNAME%</span> izeneko zure proiektua iraungi izana ezarritako epean gutxieneko laguntzarik jaso gabe.</p>\r\n<p>Aurrera jarraitzeko eta esperientzia honetatik ikasteko, apartakoa izango zen proiektuen sustatzaileei zuzendutako <a href=\"https://docs.google.com/spreadsheet/viewform?formkey=dFNqcHctWXNFMHdoMUtRbHpDZmUwc2c6MQ#gid=0 \">galdetegi labur honi</a>pare bat minutu emango bazenizkio, geroko kanpainetako prestakuntza eta jarraipenetan hobetzeko lagungarri izatea espero dugu.</p>\r\n<p>Espero dezagun, nolanahi ere, esperientzia on gertatu izana, eta gonbita egiten dizugu Goteon parte hartzen jarraitzeko eta Goteo zuri baliagarri gertatzeko moduei buruz gogoeta egiteko.</p>\r\n<p>Zure proiektuaren laburpena <span class=\"message-highlight-blue\"><a href=\"%SUMMARYURL%\">Dashboard</a></span>en kontsulta dezakezu.</p>\r\n<p>Adeitasunez,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (21,'fr','Le projet %PROJECTNAME% a expiré','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Nous sommes désolés de que le projet  <span class=\"message-highlight-blue\">%PROJECTNAME%</span> ait expiré sans un minimum de soutien dans les délais.</p>\r\n<p>Pour aller de l\'avant et d\'apprendre de l\'expérience, ce serait formidable si vous pouvez dédier quelques minutes à remplir  <a href=\"https://docs.google.com/spreadsheet/viewform?formkey=dFNqcHctWXNFMHdoMUtRbHpDZmUwc2c6MQ#gid=0 \">ce court questionnaire</a> , que nous avons préparé pour les porteurs de projets, et qui pourrait nous aider à améliorer la préparation et le suivi des campagnes à venir.</p>\r\n<p>Nous espérons aussi que vous ayez eu une bonne expérience, et nous vous invitons à continuer à participer sur Goteo et à penser différentes façons de vous en servir. \r\n</p>\r\n<p>Vous pouvez voir le résumé du projet sur votre  <span class=\"message-highlight-blue\"><a href=\"%SUMMARYURL%\">Tableau de bord</a></span>.</p>\r\n<p>Cordialemente,</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (22,'en','The project, %PROJECTNAME%, has completed the second round','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Your project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, has completed the second round.</p>\r\n<p>Remember that you can manage your rewards and the co-financiers of your project from your <span class=\"message-highlight-blue\"><a href=\"%REWARDSURL%\">Dashboard</a></span>.</p>\r\n<p>Before long, we will write you again in relation to the collective benefits of the project as an open reward promised in the campaign, informing you about how to make it accessible via Goteo once they are published online.</p>\r\n<p>Thank you very much!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
