insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (24,'fr','Le projet %PROJECTNAME% inactif','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Vous n\'avez rien dit sur votre projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> au cours des trois derniers mois. Vous devriez rester en contact avec votre communauté.</p>\r\n<p>Nous vous rappelons que vous pouvez publier des nouvelles sur votre projet à partir de votre tableau de bord <span class=\"message-highlight-blue\"><a href=\"%UPDATESURL%\">Dashboard</a></span>.</p>\r\n<p>On continue!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',1);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (25,'ca','El projecte %PROJECTNAME% fa 2 mesos que va ser cofinançat','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Ja fa dos mesos que el projecte <span class=\"message-highlight-blue\">%PROJECTNAME%</span> va ser cofinançat. Hauries de revisar els retorns i recompenses pendents, per comprovar si pot haver-hi encara algú pendent de rebre el que vàreu prometre a canvi del seu recolzament.</p>\r\n<p>Et recordem que pots gestionar les recompenses des del teu <span class=\"message-highlight-blue\"><a href=\"%REWARDSURL%\">panell de control</a></span>.</p>\r\n<p>En cas d\'haver publicat ja online el retorn col.lectiu que s\'especifica a la campanya, hauries d\'actualitzar aquesta informació des d\'<a href=\"http://goteo.org/dashboard/projects/commons\">aquest apartat</a>, marcant la casella corresponent un cop introduïda l\'adreça d\'Internet (URL sencera).</p>\r\n<p>Quan ho hagis fet el teu projecte passarà a formar part de la nostra secció <a href=\"http://goteo.org/discover/view/fulfilled\">‘Missió complerta’</a></p>\r\n<p>Seguim en contacte!</p>\r\n<p>L\'equip de Goteo</p>\r\nhttp://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (25,'en','It\'s been two months since the project, %PROJECTNAME%, was financed','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>It\'s already been two months since your project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, was financed. You should go over the pending rewards and returns.</p>\r\n<p>Remember that you can manage rewards from your <span class=\"message-highlight-blue\"><a href=\"%REWARDSURL%\">Dashboard</a></span>.</p>\r\n<p>In case you have already generated the collective benefit specified in your campaign, you should update that information from  <a href=\"http://goteo.org/dashboard/projects/commons\">this section</a>, checking the box accordingly after introducing its Internet address (complete URL).</p>\r\n<p>Once you\'ve completed this info, your project will be visualized in our <a href=\"http://goteo.org/discover/view/fulfilled\">\'Accomplished\' section</a>.</p>\r\n<p>Keep in touch!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
