insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (26,'fr','Vous pouvez déjà traduire le projet %PROJECTNAME%','<p>Bonjour <span class=\"message-highlight-red\">%OWNERNAME%</span>,</p>\r\n<p>S\'il vous plaît, notez que le projet <span class=\"message-highlight-blue\">%PROJECTNAME%</span> a été activé pour être traduit.</p>.\r\n<p>Vous pouvez le trouver dans le tableau de bord, à partir de:</p>\r\n<p><span class=\"message-highlight-blue\"><a href=\"%SITEURL%/dashboard/translates\">%SITEURL%/dashboard/translates</a></span></p>\r\n<p>Une fois là, vous pouvez sélectionner la langue (ou les langues) à traduire dans le menu déroulant, et les modifications seront automatiquement enregistrées. Depuis le menu horizontal vert, vous pouvez accéder aux différentes sections de la campagne (la description, le coût, la retombée, etc.)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nLe financement collectif à l\'ADN libre<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (27,'en','Obtain your Goteo.org donation certificate in 5 minutes','<p>Hi,<br>\r\n<br>\r\nAs you probably already know, contributing to Goteo not only makes possible a whole bunch of project that benefit us all, but it is also tax deductible!</p>\r\n<p>\r\nWe have been working hard to make it even easier for you:\r\n<br>\r\nStarting today, and <strong>up to December the 31st</strong>, you can fill out your details at Goteo.org or modify them in order to download the <strong>donation certificate</strong> relative to all the contributions you have made throughout 2013 through our web platform.<br>\r\n<br>\r\nUse this document to reclaim the corresponding deduction in your tax declaration, or in your Income Tax, in the case of individuals.\r\n</p>\r\n\r\n<p>\r\n<strong>How to download the certificate</strong>\r\n</p>\r\n\r\n<p>1. Login into Goteo.org with your username and password<br>\r\nIn case you don\'t remember your login details, in order to recover your password you will at least know your username or email address used and ask for password recovery at this link: <a href=\"http://goteo.org/user/recover\">http://goteo.org/user/recover</a><br>\r\n</p> <p>\r\n2. In your user panel, go to the \"My Activity / Donation Certificate\" tab, or click on the following link: <a href=\"http://goteo.org/dashboard/activity/donor\">http://goteo.org/dashboard/activity/donor</a></p>\r\n<p> 3. Review and, if necessary, correct your details:<br>\r\nIMPORTANT: bear in mind that if these details are not correct, the certificate will have no fiscal value.<br>\r\n  •	Name and surname of the individual or address of the organisation.<br>\r\n  •	Tax ID / VAT Number<br>\r\n  •	Address (street, city/town, postcode, country) </p> \r\n<p>\r\n\r\n4. Once your details are correct, tick the \"Accept\" box and then you will be able to access the \"Download the certificate\" button<br> IMPORTANT: bear in mind that, once you click on \"Accept\", you will no longer be able to modify your details.</p>\r\n\r\n<p>\r\nIf you have made more than one contribution in Goteo.org, you will obtain a single certificate with the sum of all of them, and information updated to the date they were made on.<br>\r\nThe certificate is issued by the Open Sources Foundation (<a href=\"http://www.fuentesabiertas.org\">http://www.fuentesabiertas.org</a>), the organisation which manages Goteo.org and receives the donations destined to the project or projects which you have supported with this user.<a href=\"http://www.fuentesabiertas.org\"></a></p>\r\n<p>Should you have any doubt or problem on the subject of donation certificates, you can contact us at: <strong>donate@goteo.org</strong><br>\r\n</p>\r\n<p>\r\nOnce again, thank you for your support. ;-)\r\n\r\n</p>\r\n\r\nThe Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective co-financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ntwitter: @goteofunding</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (28,'en','Thanks for your donation toward the %PROJECTNAME% project','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Thanks for co-financing the project, <span class=\"message-highlight-blue\">%PROJECTNAME%</span>, in the amount of %AMOUNT% €.</p>\r\n<p>We want to remind you that your donation is tax-deductible in the EU.</p>\r\n<p>Thanks again for your generosity!</p>\r\n<p>--<br />\r\n<p>Stay in touch! :)</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
