insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (64,'ca','Gràcies per cofinançar el projecte %PROJECTNAME%','<p>Hola <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Moltes gràcies per cofinançar el projecte <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> amb %AMOUNT% €. %DROPED%</p>%REWARDS% %METHOD%\r\n<p>Et recordem que el teu aport és una donació desgravable en la declaració a Hisenda, tal com t\'expliquem aquí:<a href=\"https://goteo.org/faq/investors#q112\">https://goteo.org/faq/investors#q112</a></p>\r\n\r\n<p>Que tinguis un bon dia!</p>\r\n<p>The Goteo Mailer<br>\r\nhttps://goteo.org/<br>\r\nFinançament col.lectiu amb ADN obert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\ninfo@goteo.org<br>\r\ntwitter/identica: @goteofunding',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (64,'en','Thank you for co-financing the %PROJECTNAME% project','<p>Dear <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Thank you very much for co-financing the <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> project in the amount of %AMOUNT% €. %DROPED%</p>\r\n%REWARDS% %METHOD%\r\n<p>We want to remind you that your donation is tax-deductible in Spain (not yet in the rest of European countries, but we are working on it building alliances and strategies focused on policy makers).</p>\r\n<p>Have a good day!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nCollective financing with open DNA<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (64,'eu','Eskerrik asko %PROJECTNAME% proiektua kofinantziatzeagatik','<p>Kaixo <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Eskerrik asko <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> proiektua %AMOUNT% eurorekin kofinantziatzeagatik. %DROPED%</p>%REWARDS% %METHOD%\r\n<p>Proiektuak finkatzen duen banakako sariari uko egin diozunez, gogorarazten dizugu hori zergetan kenkari gisa aplika dezakezun dohaintza dela.</p>\r\n<p>Egun ona izan dezazula!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nDNA irekia duen finantzaketa kolektiboa<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (64,'fr','Merci pour cofinancer le projet %PROJECTNAME%','<p>Bonjour <span class=\"message-highlight-red\">%USERNAME%</span>,</p>\r\n<p>Merci beaucoup pour cofinancer le projet <span class=\"message-highlight-blue\"><a href=\"%PROJECTURL%\">%PROJECTNAME%</a></span> avec %AMOUNT% €. %DROPED%</p>%REWARDS% %METHOD%\r\n<p>Comme vous avez renoncé à la récompense individuelle offerte par le projet, nous vous rappelons qu\'il s\'agit d\'un don déductible d\'impôt si vous êtes résident en Espagne.</p>\r\n<p>Nous vous souhaitons une bonne journée!</p>\r\n<p>The Goteo Mailer<br>\r\nhttp://goteo.org/<br>\r\nFinancement colectif à l\'ADN ouvert<br>\r\nCrowdfunding the Commons<br>\r\n- - - - - - - - - - - - - - - - - - - -<br>\r\nhola@goteo.org<br>\r\ntwitter/identica: @goteofunding\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (65,'ca','Moltes gràcies per carregar el teu moneder Goteo!','Hola %USERNAME%! <p>El teu moneder virtual a Goteo acaba de ser recarregat amb %AMOUNT_RECHARGUED%.</p><p>Ara mateix disposes d\'un total de %AMOUNT_POOL% al <a href=\"%WALLET_URL%\">teu moneder</a> per fer donacions quan vulguis a projectes dins Goteo. Veuràs aquesta opció cada vegada que contribueixis a una campanya, entre les modalitats de pagament disponibles.</p><p>Et recordem que la càrrega de crèdit a través de la nostra Fundació Goteo atorga la possibilitat de <a href=\"%CERTIFICATE_URL%\">descarregar el certificat de desgravació fiscal corresponent</a>.</p>',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (66,'ca','Et recordem que la campanya %PROJECTNAME% està acabant','Hola %USERNAME%!\r\n\r\n<p>\r\nEt recordem que a la campanya <a href=\"%PROJECTURL%\">%PROJECTNAME%</a> li queden %DAYS_LEFT% dies i %MONEY_LEFT% per assolir el seu pressupost mínim i necessita el teu ajut per aconseguir-ho.\r\n</p>\r\n\r\n<p>Dóna ara i comparteix-ho a les xarxes socials per tal que el teu entorn més proper també s’animi a ajudar-los.</p>\r\n\r\n<p>Gota a gota… s\'omple la bota! :)</p>\r\n\r\n<p>L’equip de Goteo</p>\r\n',0);
insert  into `template_lang`(`id`,`lang`,`title`,`text`,`pending`) values (66,'en','We remind you that the project %PROJECTNAME% is finishing','Hi %USERNAME%! \r\n\r\n<p>We remind you that there are %DAYS_LEFT% days left for the campaign <a href=\"%PROJECTURL%\">%PROJECTNAME%</a> to achieve their minimum funding goal and it means %MONEY_LEFT% more to go. They need your support to achieve it.\r\n</p>\r\n\r\n<p>Please donate now and share through social media. Maybe your closest friends will also take a look and help them on their goal.</p>\r\n\r\n<p>You know, little by little… we all can make it! ;)</p>\r\n\r\n<p>Goteo’s team.</p>\r\n',0);
